    console.log(login);
  let url="http://192.168.1.75:8070/eliminar/"+login;
  const accessToken = localStorage.getItem("token");


  if (accessToken) {

    const headers = {
      Authorization: `Bearer ${accessToken}`,
    };
    axios.delete(url,{headers})
    .then(response=>{

    })}
}
